      out.write("\r\n");
      out.write("                    async function sendMessage() {\r\n");
      out.write("                        const input = document.getElementById('chatInput');\r\n");
      out.write("                        const msg = input.value.trim();\r\n");
      out.write("                        if (!msg) return;\r\n");
      out.write("\r\n");
